import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { library } from '@fortawesome/fontawesome-svg-core';
import { faHeartCirclePlus, faHeartCircleMinus, faHeart } from '@fortawesome/free-solid-svg-icons';

library.add(faHeartCircleMinus, faHeartCirclePlus, faHeart);

function Quotation(props) {
    const [myState, setMyState] = useState({ counter: props.count });
    function addCount() {
        setMyState(pre => {
            return { counter: pre.counter + 1 }
        })
    }
    function subCount() {
        setMyState(pre => {
            return { counter: pre.counter - 1 }
        })
    }

    return (
        <div className="quoteStyle">
            <div class="heartCounter">
                <span class="heartIcon"><FontAwesomeIcon icon="heart" /></span>
                <span class="count">{myState.counter}</span>
            </div>
            <div className="quoteText">
                <p>{props.text}</p>
                <p>{props.name}</p>
            </div>
            <div class="plusMinusIcon">
                <span class="heartPlusIcon">
                    <FontAwesomeIcon icon="heart-circle-plus" onClick={addCount} />
                </span>
                <span class="heartMinusIcon">
                    <FontAwesomeIcon icon="heart-circle-minus" onClick={subCount} />
                </span>
            </div>

        </div>
    );
}

export default Quotation;
