import "./reminder.css";
import { useEffect, useState } from 'react';

const Reminder = () => {
    const [posts, setPosts] = useState([]);
    let [isLoaded, setIsLoaded] = useState(false);
    let [err, setErr] = useState(null);

    useEffect(() => {
        const getData = () => {
            fetch('http://localhost:9999/posts')
                .then(res => {
                    if (res.status >= 400) {
                        throw new Error("Server responds with error");
                    }
                    return res.json();
                })
                .then(resdata => {
                    setIsLoaded(true);
                    setPosts(resdata);
                }, err => {
                    setErr(err);
                    setIsLoaded(true);
                })
        };
        getData()
    }, [])
    function getTime(posts) {
        let day1 = 0;
        let day2 = 0;
        let hour1 = 0;
        let hour2 = 0;
        let minute1 = 0;
        let minute2 = 0;
        let min = Number.MAX_VALUE;
        let index = 0;
        let dateNow = new Date();
        for (let i = 0; i < posts.length; i++) {
            if (posts[i].status === "Not done") {
                let date = new Date(posts[i].deadline);
                let seconds = Math.floor((date - dateNow) / 1000);
                let minutes = Math.floor(seconds / 60);
                if (minutes < min) {
                    min = minutes;
                    index = i;
                }
            }
            let dateTime = new Date(posts[index].deadline);
            let seconds = Math.floor((dateTime - dateNow) / 1000);
            let minutes = Math.floor(seconds / 60);
            let hours = Math.floor(minutes / 60);
            let days = Math.floor(hours / 24);
            day1 = days / 10;
            day2 = days % 10;
            hour1 = hours / 10;
            hour2 = hours % 10;
            minute1 = minutes / 10;
            minute2 = minutes % 10;
            return { day1, day2, hour1, hour2, minute1, minute2 };
        }
    }
    if (err) {
        return <div>Error: {err.message}</div>;
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <div className="reminder">
                <div className="reminder-title">
                    <p>Please finish your assignment</p>
                </div>
                <div className="reminder-time">
                    <div className="reminder-time-day">
                        <div>
                            <span>0</span>
                            <span>0</span>
                        </div>
                        <p>days</p>
                    </div>
                    <div className="reminder-time-hour">
                        <div>
                            <span>0</span>
                            <span>0</span>
                        </div>
                        <p>hours</p>
                    </div>
                    <div className="reminder-time-minute">
                        <div>
                            <span>0</span>
                            <span>0</span>
                        </div>
                        <p>minutes</p>
                    </div>
                </div>
            </div>
        );
    }
}

export default Reminder;
