import "./progress.css";
import { useEffect, useState } from 'react';

const Progress = () => {
    const [posts, setPosts] = useState([]);
    let [isLoaded, setIsLoaded] = useState(false);
    let [err, setErr] = useState(null);

    useEffect(() => {
        const getData = () => {
            fetch('http://localhost:9999/posts')
                .then(res => {
                    if (res.status >= 400) {
                        throw new Error("Server responds with error");
                    }
                    return res.json();
                })
                .then(resdata => {
                    setIsLoaded(true);
                    setPosts(resdata);
                    let [width1, width2, width3, width4] = getWidthStyleValue(posts);
                }, err => {
                    setErr(err);
                    setIsLoaded(true);
                })
        };
        getData()
    }, [])
    function getWidthStyleValue(posts) {
        let width1 = posts[0].progress;
        let width2 = posts[1].progress;
        let width3 = posts[2].progress;
        let width4 = posts[3].progress;
        return [width1, width2, width3, width4];
    }
    let [width1, width2, width3, width4] = getWidthStyleValue(posts);

    if (err) {
        return <div>Error: {err.message}</div>;
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <div className="progress">
                <div className="progress-title">
                    <h5>Assignments</h5>
                </div>
                <div className="progress-content">
                    <div className="progress-content-subject1">
                        <div className="progress-content-subject1-title">
                            <span>{posts[0].name}</span>
                            <div>
                                <span>{posts[0].progress}</span>
                            </div>
                        </div>
                        <div className="progress-content-subject1-container">
                            <div className="progress-content-subject1-container-bar" style={{width:width1}}>
                            </div>
                        </div>
                    </div>
                    <div className="progress-content-subject2">
                        <div className="progress-content-subject2-title">
                            <span>{posts[1].name}</span>
                            <div>
                                <span>{posts[1].progress}</span>
                            </div>
                        </div>
                        <div className="progress-content-subject2-container">
                            <div className="progress-content-subject2-container-bar" style={{width:width2}}>
                            </div>
                        </div>
                    </div>
                    <div className="progress-content-subject3">
                        <div className="progress-content-subject3-title" >
                            <span>{posts[2].name}</span>
                            <div>
                                <span>{posts[2].progress}</span>
                            </div>
                        </div>
                        <div className="progress-content-subject3-container">
                            <div className="progress-content-subject3-container-bar" style={{width:width4}}>
                            </div>
                        </div>
                    </div>
                    <div className="progress-content-subject4">
                        <div className="progress-content-subject4-title">
                            <span>{posts[3].name}</span>
                            <div>
                                <span>{posts[3].progress}</span>
                            </div>
                        </div>
                        <div className="progress-content-subject4-container">
                            <div className="progress-content-subject4-container-bar" style={{width:width3}}>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Progress;
