import "./note.css";
import { useEffect, useState } from 'react';

const Note = () => {

    const [posts, setPosts] = useState([]);

    useEffect(() => {
      fetch('http://localhost:9999/posts')
        .then(res => {
          return res.json();
        })
        .then(resdata => {
          console.log(resdata)
          setPosts(resdata)
        })
    }, [])

    function getMinIndex(posts){
        let dateNow = new Date();
        let min = Number.MAX_VALUE;
        let index = 0;
        for (let i = 0; i < posts.length; i++) {
            let date = new Date(posts[i].deadline);
            let seconds = Math.floor((date - dateNow) / 1000);
            let minutes = Math.floor(seconds / 60);
            if (minutes < min) {
                min = minutes;
                index = i;
            }
        }
        return index;
    }

    let minIndex = getMinIndex(posts);

    return(
        <div className="note">
            <div className="note-title">
                <span>{posts[minIndex].name}</span>
                <div className="note-title-date">
                    ~ <span>00</span>:<span>00</span> <span>7</span>/<span>11</span>
                </div>
            </div>
            <hr/>
            <div className="note-content">
                <h5>Note</h5>
                <p>{posts[minIndex].description}</p>
            </div>
        </div>
    )
}

export default Note;
